mapper.select:
SELECT

FROM t_sale_chance AS s

    s.is_valid=1

        AND s.customer_name like concat('%',#{customerName},'%')


        AND s.state=#{state}


        AND s.dev_result=#{devResult}


        AND s.create_date <=#{createDate}

mapper.update:
UPDATE t_sale_chance set is_valid=0
WHERE id in (

	  #{item}

)

mapper.select:
select

from t_sale_chance s
where s.id = #{id,jdbcType=INTEGER}
